function check_score () {
    if (info.score() == 11) {
        game.showLongText("you have 11 points", DialogLayout.Center)
    } else if (info.score() == 10) {
        game.showLongText("you have 10 points", DialogLayout.Center)
    } else if (info.score() == 9) {
        game.showLongText("you have 9 points", DialogLayout.Center)
    } else if (info.score() == 8) {
        game.showLongText("you have 8 points", DialogLayout.Center)
    } else if (info.score() == 7) {
        game.showLongText("you have 7 points", DialogLayout.Center)
    } else if (info.score() == 6) {
        game.showLongText("you have 6 points", DialogLayout.Center)
    } else if (info.score() == 5) {
        game.showLongText("you have 5 points", DialogLayout.Center)
    } else if (info.score() == 4) {
        game.showLongText("you have 4 points", DialogLayout.Center)
    } else if (info.score() == 3) {
        game.showLongText("you have 3 points", DialogLayout.Center)
    } else if (info.score() == 2) {
        game.showLongText("you have 2 points", DialogLayout.Center)
    } else if (info.score() == 1) {
        game.showLongText("you have 1 point", DialogLayout.Center)
    } else if (info.score() == 0) {
        game.showLongText("you have 0 points", DialogLayout.Center)
    } else if (info.score() == 12) {
        game.showLongText("you have 12 points", DialogLayout.Center)
    } else if (info.score() == 13) {
        game.showLongText("you have 13 points", DialogLayout.Center)
    } else if (info.score() == 14) {
        game.showLongText("you have 14 points", DialogLayout.Center)
    }
    game.over(true)
}
